import createStore from "unistore";
import axios from "axios";

const initialState = {
  token: "",
  username: "",
  password: "",
  is_login: false,
  nama : "",
  email : "",
  nomor_telepon : "",
  pesan : "",
  iata_asal: "",
  airport_id_asal: "",
  airport_name_asal: "",
  nation_code_asal: "",
  iata_tujuan: "",
  airport_id_tujuan: "",
  airport_name_tujuan: "",
  nation_code_tujuan: "",
  flight_list: [],
  weather: "",
  code: "",
  weather_code: "",
  temperature: "",
  sunrise: "",
  sunset: "",
  rate: "",
  base_currency: "",
  target_currency: "",
  new_sum: "",
  base_sum: "",
  word: "",
};

export const store = createStore(initialState);

export const actions = store => ({
  setField: (state, event) => {
    return { [event.target.name]: event.target.value };
  },

  postLogout: state => {
    return { is_login: false };
  },

  postLogin: async state => {

    const data = { username: state.username, password: state.password };
    console.log ("ini data login", data)    
    const headers = {headers: {"X-Requested-With" : "https://api.halocuk.web.id"}}
    await axios
      .post("http://localhost:8010/proxy/user/login", data)
      .then(response => {
        console.log ("ini data login", data)
        console.log("post loginnnnnn!!!!", response.data);
        if (response.data.hasOwnProperty("Token")) {
          store.setState({
            token: response.data.token,
            is_login: true,
          });
        }
      })
      .catch(function(error) {
        console.log(error);
      });
  },

  postKontakKami: async state => {

    const data = { nama: state.nama, email: state.email, nomor_telepon : state.nomor_telepon, pesan : state.pesan };
    console.log ("ini data login", data)    
    const headers = {headers: {"X-Requested-With" : "https://api.halocuk.web.id"}}
    await axios
      .post("http://localhost:8010/proxy/user/login", data)
      .then(response => {
        console.log ("ini data login", data)
        console.log("post loginnnnnn!!!!", response.data);
        if (response.data.hasOwnProperty("Token")) {
          store.setState({
            token: response.data.token,
            is_login: true,
          });
        }
      })
      .catch(function(error) {
        console.log(error);
      });
  },

  setAsal: (state, e) => {
    store.setState({ iata_asal: e.target.value });
  },

  setTujuan: (state, e) => {
    store.setState({ iata_tujuan: e.target.value });
  },

  setCurrAsal: (state, e) => {
    store.setState({ base_currency: e.target.value });
  },

  setCurrTujuan: (state, e) => {
    store.setState({ target_currency: e.target.value });
  },

  //get new currency
  getNewCurrency: async (state, moneysum) => {
    await axios
      .get("https://api.exchangeratesapi.io/latest?base=" + state.base_currency)
      .then(function(response) {
        store.setState({ rate: response.data.rates[state.target_currency] });
        const new_value = state.rate * moneysum;
        store.setState({ new_sum: new_value });
      })
      .catch(function(error) {
        console.log(error);
      });
  },

  //get weather
  getWeather: async state => {
    await axios
      .get(
        "http://api.weatherbit.io/v2.0/current?lat=" +
          state.latitude_tujuan +
          "&lon=" +
          state.longitude_tujuan +
          "&key=aa3c976285c64dafa3662f2e99d4bd03"
      )
      .then(function(response) {
        store.setState({
          weather: response.data.data[0].weather.description,
          temperature: response.data.data[0].temp,
          sunrise: response.data.data[0].sunrise,
          sunset: response.data.data[0].sunset,
          weather_code: response.data.data[0].weather.code
        });
      })
      .catch(function(error) {
        console.log(error);
      });
  },

  //get api data for flight and currency
  getData: async state => {
    await axios
      .all([
        axios.get(
          "https://aviation-edge.com/v2/public/airportDatabase?key=57d91e-653b78&codeIataAirport=" +
            state.iata_asal
        ),
        axios.get(
          "https://aviation-edge.com/v2/public/airportDatabase?key=57d91e-653b78&codeIataAirport=" +
            state.iata_tujuan
        ),
        axios.get(
          "http://aviation-edge.com/v2/public/timetable?key=57d91e-653b78&type=departure&status=scheduled&iataCode=" +
            state.iata_asal
        ),
        axios.get(
          "https://api.exchangeratesapi.io/latest?base=" + state.base_currency
        )
      ])
      .then(
        axios.spread(function(response1, response2, response3, response4) {
          store.setState({
            airport_id_asal: response1.data[0].airportId,
            airport_name_asal: response1.data[0].nameAirport,
            nation_code_asal: response1.data[0].codeIso2Country,
            latitude_asal: response1.data[0].latitudeAirport,
            longitude_asal: response1.data[0].longitudeAirport
          });
          store.setState({
            airport_id_tujuan: response2.data[0].airportId,
            airport_name_tujuan: response2.data[0].nameAirport,
            nation_code_tujuan: response2.data[0].codeIso2Country,
            latitude_tujuan: response2.data[0].latitudeAirport,
            longitude_tujuan: response2.data[0].longitudeAirport
          });
          store.setState({ flight_list: response3.data });
          store.setState({ rate: response4.data.rates.USD });
        })
      )
      .catch(function(error) {});
  }
});
