import React, { Component } from "react";
import axios from "axios";
import { withRouter } from "react-router-dom";
import "./assets/css/Login.css";

class LoginPage extends Component {
    state = {
        username : "",
        password : ""
    }
    doLogin = () => {
        this.props.postLogin().then(() => {
            if (this.props.is_login === true){
                var username = this.props.username;
                var password = this.props.password;
            }
        });
    }
  render() {
    return (
        <section>
            <div className="container">
                <div className="row">
                    <div className="col-sm-9 col-md-7 col-lg-5 mx-auto">
                        <div className="card card-signin my-5">
                            <div className="card-body">
                                <h5 className="card-title text-center">Login</h5>
                                <form className="form-signin" onSubmit={event => event.preventDefault()}>
                                    <div className="form-label-group">
                                        <input type="text" id="inputUsername" className="form-control" placeholder="Username"
                                            required autoFocus onChange={event =>this.props.setField(event)}/>
                                        <label htmlFor="inputUsername">Username</label>
                                    </div>

                                    <div className="form-label-group">
                                        <input type="password" id="inputPassword" className="form-control" placeholder="Password"
                                            required onChange={event =>this.props.setField(event)}/>
                                        <label htmlFor="inputPassword">Password</label>
                                    </div>

                                    <div className="custom-control custom-checkbox mb-3">
                                        <input type="checkbox" className="custom-control-input" id="customCheck1"/>
                                        <label className="custom-control-label" htmlFor="customCheck1">Remember Me</label>
                                    </div>
                                    <button className="btn btn-lg btn-primary btn-block text-uppercase" type="submit" onClick={() => this.postLogin()}>Login</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
  }
}

export default withRouter (LoginPage);
