import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./assets/css/Login.css";

class LoginPage extends Component {
    constructor(props) {
        super(props);
        this.state = { username: "", password: "" };
      }

      validateForm() {
        return this.state.username.length > 0 && this.state.password.length > 0;
      }

      changeInput = event => {
        this.setState({ [event.target.name]: event.target.value });
      };
      postLogin = () => {
        const { username, password } = this.state;
        const data = {
          username: username,
          password: password
        };
        const self = this;
        axios
          .post("https://api-todofancy.herokuapp.com/api/auth", data)
          .then(function(response) {
            localStorage.setItem("token", response.Token);
            localStorage.setItem("is_login", true);
            self.props.history.push("/");
          })
          .catch(function(error) {
            console.log(error);
          });
          console.log(this.props.is_login);
      };

  render() {
    return (
        <section>
            <div className="container">
                <div className="row">
                    <div className="col-sm-9 col-md-7 col-lg-5 mx-auto">
                        <div className="card card-signin my-5">
                            <div className="card-body">
                                <h5 className="card-title text-center">Login</h5>
                                <form className="form-signin" onSubmit={event => event.preventDefault()}>
                                    <div className="form-label-group">
                                        <input type="text" id="inputUsername" className="form-control" placeholder="Username"
                                            required autofocus onChange={event => this.changeInput(event)}/>
                                        <label for="inputUsername">Username</label>
                                    </div>

                                    <div className="form-label-group">
                                        <input type="password" id="inputPassword" className="form-control" placeholder="Password"
                                            required onChange={event => this.changeInput(event)}/>
                                        <label for="inputPassword">Password</label>
                                    </div>

                                    <div className="custom-control custom-checkbox mb-3">
                                        <input type="checkbox" className="custom-control-input" id="customCheck1"/>
                                        <label className="custom-control-label" for="customCheck1">Remember Me</label>
                                    </div>
                                    <button className="btn btn-lg btn-primary btn-block text-uppercase" type="submit" onClick={() => this.postLogin()} disabled={!this.validateForm()}>Login</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
  }
}

export default LoginPage;
